<script setup>
import ApplicationLogo from '@/Components/ApplicationLogo.vue';
import Checkbox from '@/Components/Checkbox.vue';
import GuestLayout from '@/Layouts/GuestLayout.vue';
import InputError from '@/Components/InputError.vue';
import InputLabel from '@/Components/InputLabel.vue';
import PrimaryButton from '@/Components/PrimaryButton.vue';
import TextInput from '@/Components/TextInput.vue';
import { Head, Link, useForm } from '@inertiajs/vue3';

defineProps({
    canResetPassword: Boolean,
    status: String,
});

const form = useForm({
    email: '',
    password: '',
    remember: false
});

const submit = () => {
    form.post(route('login'), {
        onFinish: () => form.reset('password'),
    });
};
</script>

<template>

    <Head title="Log in" />

    <GuestLayout>
        <div class="flex flex-col lg:flex-row bg-white rounded-lg shadow-lg overflow-hidden max-w-4xl mx-auto">
            <!-- Formulario (Izquierda) -->
            <div class="w-full lg:w-1/2 p-8">
                <Link href="/" class="flex justify-center mb-6">
                <img src="data:image/jpeg;base64,"
                    alt="Login Image" class="w-auto h-auto object-cover"> </Link>

                <div v-if="status" class="mb-4 text-sm font-medium text-green-600 text-center">
                    {{ status }}
                </div>

                <form @submit.prevent="submit" class="space-y-4">
                    <div>
                        <InputLabel for="email" value="Correo electrónico" />
                        <TextInput id="email" type="email" class="mt-1 block w-full" v-model="form.email" required
                            autofocus autocomplete="username" />
                        <InputError class="mt-2" :message="form.errors.email" />
                    </div>

                    <div>
                        <InputLabel for="password" value="Contraseña" />
                        <TextInput id="password" type="password" class="mt-1 block w-full" v-model="form.password"
                            required autocomplete="current-password" />
                        <InputError class="mt-2" :message="form.errors.password" />
                    </div>

                    <div class="flex justify-end items-center">


                        <Link v-if="canResetPassword" :href="route('password.request')"
                            class="text-sm text-black  hover:underline">
                        Recuperar contraseña
                        </Link>
                    </div>

                    <div>
                        <PrimaryButton class="w-full" :class="{ 'opacity-25': form.processing }"
                            :disabled="form.processing">
                            Log in
                        </PrimaryButton>
                    </div>
                </form>
            </div>

            <!-- Imagen (Derecha) -->
            <div class="w-full lg:w-1/2">
                <img src="data:image/jpeg;base64,"
                    alt="Login Image" class="w-full h-full object-cover">
            </div>
        </div>
    </GuestLayout>
</template>
